<script lang="ts" setup>
import { ref } from 'vue';
import {quit} from '@/http/auth/index'
import router from "@/router";
import {useCounterStore} from "@/store";

const item = ref('item1')
const collapsed = ref(false)
const iconUrl = ref('https://resources.hehuibin.cn/img2.png')

// 首页
const isYes = ref(false)
const isNo = ref(true)

const scrollHandler = (e: any) => console.log(e);

const changeCollapsed = () => {
  isNo.value = !isNo.value
  isYes.value = !isYes.value
  collapsed.value = !collapsed.value;

  iconUrl.value = collapsed.value
      ? 'https://resources.hehuibin.cn/img1.png'
      : 'https://resources.hehuibin.cn/img2.png';
};

const quits = () => {
  quit().then(item => {
    if (item.data == true){
      useCounterStore().user.tok = "";
      useCounterStore().user.role = "";
      useCounterStore().user.password = "";
      router.push("/")
    }
  })
}

const changeHandler = (active: any) => {
  console.log('change', active);
  item.value = active
};
</script>

